package ojLeetCode;

import java.util.HashMap;
import java.util.Map;

/**
 * Created by Mryang on 15-5-12.
 */
public class Leet38 {

    public static void main(String[] args) {
        Map<String,String> hm = new HashMap<String, String>();
        hm.put("1","11");
        hm.put("11","21");
        hm.put("2","12");
//        String r = getSay("");//countAndSay(25);
        String r = countAndSay(6);
        System.out.println(r);
    }

    public static String countAndSay(int n) {
        String r = "1";
        for (int i=1;i<n;i++){
            r = getSay(r);
        }
        return r;
    }
    public static String getSay(String str){
        StringBuffer r = new StringBuffer("");
        char[] cs = str.toCharArray();
        int p1=0,p2=0;
        char x = cs[0];
        //用两个指针遍历字符串数组，如111221，统计出连续出现的字符以及其个数，并加入sb中。
        // 如1--出现的下标对应的是0,3，sb就加入（3-0）和1
        for (p2=0;p2<cs.length;p2++){
            if (cs[p2] != x){
                r.append((p2-p1)).append(x);
                p1 = p2;
                x = cs[p2];
            }
        }
        r.append((p2-p1)).append(x);
        return r.toString();
    }
}
